mapper.select:
SELECT * FROM( SELECT rownum rn, cd.drain_date AS "date", cd.reason AS "reason", c.name AS "customerName", con.name AS "managerName" FROM customer_drains cd LEFT OUTER JOIN customers c ON cd.customer_id = c.id LEFT OUTER JOIN contacts con ON c.manager_id = con.id
)
WHERE rn >= #{firstIndex} AND rn < #{endIndex}

SELECT * FROM( SELECT t.*, rownum rn FROM( SELECT c.customer_level AS "level", count(*) AS "num" FROM customers c INNER JOIN orders o ON o.customer_id = c.id WHERE customer_level IS NOT NULL
GROUP BY customer_level ORDER BY customer_level ) t )
WHERE rn >= #{firstIndex} AND rn < #{endIndex}

SELECT *
FROM(
	SELECT t.*, rownum rn
	FROM(
		SELECT ${type} AS "type", count(*) AS "num"
		FROM customers
		WHERE ${type} IS NOT NULL
		GROUP BY ${type}
		ORDER BY ${type}
	) t
)

WHERE rn >= #{firstIndex} AND rn < #{endIndex}

SELECT * FROM( SELECT rownum rn, t.* FROM( SELECT c.name AS "custName", sum(oi.item_count * p.price) AS "totalMoney" FROM customers c INNER JOIN orders o ON o.customer_id = c.id INNER JOIN order_items oi ON oi.order_id = o.id INNER JOIN products p ON oi.product_id = p.id
GROUP BY c.name ) t )
WHERE rn >= #{firstIndex} AND rn < #{endIndex}
